sys_variable_value.value:
(function execute(inputs, outputs) {

var SI = new x_arams_af_atteste.AF_AttesteraUtils().getCostCenters(inputs.Department);
var result = SI.replace('sys_idIN','').split(',');

  outputs.cost_centers_output = result;

})(inputs, outputs);

