sys_variable_value.value:
(function execute(inputs, outputs) {
var current_dept_head;
var previous_dept_head;

  var gr = new GlideRecord('cmn_department');
  gr.get(inputs.prev_department);
  var previous_dept_head = gr.dept_head.getRefRecord();


  var gr2 = new GlideRecord('cmn_department');
  gr2.get(inputs.curr_department);
  var current_dept_head = gr2.dept_head.getRefRecord();

   outputs.prev_dept_head = previous_dept_head;
   outputs.curr_dept_head = current_dept_head;

})(inputs, outputs);

