sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	//Update attest reg. with dates and status = coming or retired
	var today = new GlideDate();
	var attReg = new GlideRecord('x_arams_af_atteste_attestation_register');
	attReg.addEncodedQuery('u_attest_err='+current.sys_id);
	attReg.query();
	while (attReg.next()){
		if (attReg.getValue('u_valid_from')<= today){
			attReg.setValue('u_state','active');
			attReg.setValue('u_valid_from', today);
		}else if (attReg.getValue('u_valid_from')> today){
			attReg.setValue('u_state','coming');
		}else if (attReg.getValue('u_valid_to')< today){
			attReg.setValue('u_state','retired');
		}
		attReg.update();
	}

	var SpecAttReg = new GlideRecord('x_arams_af_atteste_special_attestation_rights');
	SpecAttReg.addEncodedQuery('u_attest_err='+current.sys_id);
	SpecAttReg.query();
	while (SpecAttReg.next()){
		if (SpecAttReg.getValue('u_valid_from')<= today){
			SpecAttReg.setValue('u_state','active');
			attReg.setValue('u_valid_from', today);
		}else if (SpecAttReg.getValue('u_valid_from')> today){
			SpecAttReg.setValue('u_state','coming');
		}else if (attReg.getValue('u_valid_to')< today){
			SpecAttReg.setValue('u_state','retired');
		}
		SpecAttReg.update();
	}



})(current, previous);
